<template>
    <div class="box-shadow3">
        <video width="100%" playsinline="" controls="controls" :ref="'video'+itemIndex">
            <source :src="item.mp4" type="video/mp4"> 
            <source :src="item.ogg" type="video/ogg">
        </video>
        <img :src="item.img" v-if="showImg" width="100%" class="nkn-news-video-cover-zh" @click="videoClick">
        <img :src="videoDown" v-if="showImg" class="img-youtube" @click="videoClick">
    </div>
</template>
<script>
    export default {
        name: 'video',
        props:{
            item:{
                type:Object,
                default:{}
            },
            itemIndex:{
                type:Number,
                default:0
            }
        },
        data () {
            return {
                showImg:true,
                videoDown:'data:image/png;base64,'
            }
        },
        methods:{
            videoClick(){
                this.showImg = false;
                this.$refs['video'+this.itemIndex].play();
            }
        }
    }
</script>
<style scoped>
    .box-shadow3{
        margin-top: 50px;
    }
    .img-youtube{
        width: 72px;
        position: absolute;
        margin: auto;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        cursor: pointer;
    }
    .nkn-news-video-cover-zh{
        position: absolute;
        width: 100%;
        left: 0;
        top: 0;
        cursor: pointer;
    }
</style>
